using namespace std;

int main() {

    // Get the size of the spiral from the user.
    int n;
    cin >> n;

    // Create a 2D array to store the spiral pattern.
    int array[n][n];

    // Initialize the array to 0.
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            array[i][j] = 0;
        }
    }

    // Start at the center of the spiral and print the number 1.
    array[n / 2][n / 2] = 1;

    // Iterate through the spiral, printing the numbers in order.
    int i = n / 2;
    int j = n / 2;
    while (i >= 0 && j >= 0) {
        // Print the number in the current cell.
        cout << array[i][j] << " ";

        // If the current cell is not the last cell in the row, increment the row index.
        if (i < n - 1) {
            i++;
        } else {
            // If the current cell is not the last cell in the column, increment the column index.
            if (j < n - 1) {
                j++;
            } else {
                // If the current cell is the last cell in the row and the column, decrement the row index.
                i--;
            }
        }

        // Increment the value in the current cell.
